#include "SimpleDelayServerImpl.h"

 
DDS_Long SimpleDelayServerImpl::suma(/*in*/ DDS_Long value1, /*in*/ DDS_Long value2) 
{
    return value1 + value2;
} 
 
Estructura SimpleDelayServerImpl::duplicate(/*in*/ const Estructura& es) 
{
    Estructura duplicate_ret;
    Estructura_initialize(&duplicate_ret);

    duplicate_ret.valor1 = es.valor1;
    duplicate_ret.valor2 = es.valor2;
    duplicate_ret.valor3 = strdup(es.valor3);

    duplicate_ret.seq.ensure_length(500,500);
    for(int i = 0; i < es.seq.length(); i++)
        duplicate_ret.seq[i] = es.seq[i];
   
    return duplicate_ret;
} 
